import React, { useState } from 'react';
import { Text, StyleSheet, View, Button } from 'react-native'
import { TextInput } from 'react-native-web';

const Versão01 = () => {
  const [valorA, setValorA] = useState(0)
  const [valorB, setValorB] = useState(0)
  const [resultado, setResultado] = useState(0)

  return (
    <View>
      <View>
        <Text style={styles.textStyle}>Valor A:</Text>
        <TextInput 
        style={styles.txtInputStyle}
        value={valorA}
        onChangeText={(text) => setValorA(text)}
        />

        <Text style={styles.textStyle}>Valor B:</Text>
        <TextInput 
        style={styles.txtInputStyle}
        value={valorB}
        onChangeText={(text) => setValorB(text)}></TextInput>
      </View>

      <View>
        <Button
          onPress={() => {
            console.log('button "Soma" pressed')
            const res = valorA + PageTransitionEvent()
            setResultado(res)
          }}
          title="Soma A + B" />

        <Button
          onPress={() => {
            console.log('button "Subtracção" pressed')

          }}
          title="Subtrai A - B" />

        <Button
          onPress={() => {
            console.log('button "Multiplicação" pressed')

          }}
          title="Multiplica A * B" />

        <Button
          onPress={() => {
            console.log('button "Divisão"pressed')

          }}
          title="Divide A / B" />
      </View>
      <Text>{resultado}</Text>
    </View>
  )
}


const styles = StyleSheet.create({
  textStyle: {
    fontSize: 36,
  },
  txtInputStyle: {
    borderColor: 'black',
    borderWidth: 2,
  },
  btnStyle: {

  }
})

// PART 4 - EXPORT THE COMPONENT SO WE CAN USE IT ELSEWHERE IN OUR PROJECT
export default Versão01
