import React, { useState } from 'react'
// IMPORT FlatList
import { Text, StyleSheet } from 'react-native'
import { TouchableOpacity, View } from 'react-native-web'

const Versão02 = () => {
  // CREATE AN ARRAY OF OBJECTS
  const [txt, setText] = useState('')
  const [txt1, setTxt1] = useState()
  const [txt2, setTxt2] = useState('')
  const [valor1, setValor1] = useState(0)
  const [valor2, setValor2] = useState(0)
  const [res, setRes] = useState(0)
  const descobreV2 = (c, n) => {
    if(c == 1){
      setTxt2(txt2 + n)
    }
    setText(txt + n)
    return('txt: ' + txt + ', txt2: ' + txt2)
  }

  return (
    <View style={styles.principalStyle}>
      <View style={styles.clearStyle}>
        <TouchableOpacity style={styles.btnClearStyle}
          onPress={() => {
            setText('')
            console.log(txt)
          }}>
          <Text style={styles.txtStyle}>C</Text>
        </TouchableOpacity>
      </View>

      <Text style={styles.fakeInputStyle}>{txt}</Text>

      <View style={styles.viewTecladoStyle}>
        <View style={styles.viewBtnNumtyle}>
          <View style={styles.viewBtnNumHorizontalStyle}>
            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                console.log(descobreV2(a, '7'))
                let a = 0
                a = 1
              }}
            >
              <Text style={styles.txtStyle}>7</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '8')
                setTxt2(txt2 + '8')

              }}>
              <Text style={styles.txtStyle}>8</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '9')
                setTxt2(txt2 + '9')

              }}>
              <Text style={styles.txtStyle}>9</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.viewBtnNumHorizontalStyle}>
            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '4')
                setTxt2(txt2 + '4')

              }}>
              <Text style={styles.txtStyle}>4</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '5')
                setTxt2(txt2 + '5')
                console.log(txt2)
              }}>
              <Text style={styles.txtStyle}>5</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '6')
                setTxt2(txt2 + '6')

              }}>
              <Text style={styles.txtStyle}>6</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.viewBtnNumHorizontalStyle}>
            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '1')
                setTxt2(txt2 + '1')

              }}>
              <Text style={styles.txtStyle}>1</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '2')
                setTxt2(txt2 + '2')

              }}>
              <Text style={styles.txtStyle}>2</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '3')
                setTxt2(txt2 + '3')

              }}>
              <Text style={styles.txtStyle}>3</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.viewBtnNumHorizontalStyle}>
            <TouchableOpacity style={styles.btnNumberStyle}
              onPress={() => {
                setText(txt + '0')
                setTxt2(txt2 + '0')

              }}>
              <Text style={styles.txtStyle}>0</Text>
            </TouchableOpacity>

            <TouchableOpacity style={styles.btnResStyle}
              onPress={() => {
                setValor2(parseFloat(txt2))
                setRes(valor1 + valor2)
                setText(res)
                console.log('valor 1: '+ valor1)
                console.log('valor 2: '+ valor2)
              }}>
              <Text style={styles.txtStyle}>=</Text>
            </TouchableOpacity>
          </View>
        </View>

        <View style={styles.ViewBtnOperadoresStyle}>
          <TouchableOpacity style={styles.btnOperadoresStyle}
            onPress={() => {
              setText(txt + '+')
              setValor1(valor1 + parseFloat(txt))
              setTxt2('')
              a = 1
              console.log(valor1)
            }}>
            <Text style={styles.txtStyle}>+</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.btnOperadoresStyle}
            onPress={() => {
              setText(txt + '-')
              console.log(txt)
            }}>
            <Text style={styles.txtStyle}>-</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.btnOperadoresStyle}
            onPress={() => {
              setText(txt + 'x')
              console.log(txt)
            }}>
            <Text style={styles.txtStyle}>x</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.btnOperadoresStyle}
            onPress={() => {
              setText(txt + '/')
              console.log(txt)
            }}>
            <Text style={styles.txtStyle}>/</Text>
          </TouchableOpacity>
        </View>
      </View>

    </View>
  )
}

const styles = StyleSheet.create({
  principalStyle: {
    padding: 20,
    width: '100%',
    height: '100%',

  },
  fakeInputStyle: {

    height: 45,
    borderWidth: 2,
    fontSize: 25,
  },
  viewTecladoStyle: {
    paddingTop: 15,
    flexDirection: 'row',
    width: '100%',
    justifyContent: 'space-between',
  },
  viewBtnNumtyle: {
    justifyContent: 'space-between',
    height: 300,
    width: '75%',
  },
  viewBtnNumHorizontalStyle: {
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  btnClearStyle: {
    backgroundColor: '#F8CECC',
    borderColor: '#C36B67',
    borderWidth: 2,
    width: 60,
    height: 60,
    textAlign: 'center',
    justifyContent: 'center',
  },
  btnNumberStyle: {
    backgroundColor: '#DAE8FC',
    borderColor: '#96B0D6',
    borderWidth: 2,
    width: 60,
    height: 60,
    textAlign: 'center',
    justifyContent: 'center',
  },
  btnResStyle: {
    backgroundColor: '#D5E8D4',
    borderColor: '#98C082',
    borderWidth: 2,
    height: 60,
    width: '60%',
    justifyContent: 'center',
    textAlign: 'center',
  },
  ViewBtnOperadoresStyle: {
    justifyContent: 'space-between',
  },
  btnOperadoresStyle: {
    backgroundColor: '#E1D5E7',
    borderColor: '#B095BC',
    textAlign: 'center',
    justifyContent: 'center',
    borderWidth: 2,
    height: 60,
    width: 60,

  },
  txtStyle: {
    fontSize: 20,
  },
  clearStyle: {
    alignItems: 'flex-end',
    paddingBottom: 15,
  }
})

export default Versão02
